    .descr {
      font-size: 1.3rem;
      margin-bottom: 0;
    }

    .btn-block {
      position: absolute;
      bottom: 50px;
      left: 50%;
      transform: translateX(-50%);
      z-index: 5;

      & .btn {
        position: relative;
      }
    }

    .btn__text {
      font-size: 1rem;
      line-height: 0;
    }
  }

  @media screen and (max-width: 560px) {
    .coffeenomica-section {
      padding-top: 20px;

      &--pattern {
        .coffeenomica-section {

          &__info {
            width: 100%;
            margin-bottom: 20px;
          }

          &__image {
            width: 100%;
          }
        }
      }

      &__container {
        min-height: auto;
        &--reverse {
          min-height: 545px;
          .coffeenomica-section {

            &__info {
              width: 100%;
            }

            &__image {
              min-height: 284px;
                // bottom: 16%;
              width: calc(100% - 16%);
            }
          }
        }
      }


      &__info {
        width: 100%;
      }

      &__image {
        width: 100%;
      }

      &__btn {
        width: calc(100% - 40px);
      }
    }

    .btn-block {
      width: calc(100% - 40px);
      bottom: 5%;
    }

    .title {

      font-size: 22px;
      line-height: 32px;
      max-width: 100%;
      width: 100%;

      &-with-decoration {
        padding: 0 56px;

        &::after, 
        &::before {
          width: 61px ;
        }
      }
    }

    .descr {
      font-size: 16px;
      line-height: 24px;
      margin-bottom: 0;
    }

    .btn {
      bottom: 2%;
      padding: 20px 22px;

      &__text {
        font-size: 1rem;
        white-space: nowrap;
      }

    }
  }

</style>>

